import { useState } from "react";

const Welcome = (props) => {
  const [name, setName] = useState(props.name);

  const saveTask = () => {
    localStorage.setItem('task', {value: name});
    console.log('saveTask');
  }


  return (
    <div className="welcome" data-testId='welcome'>
      <p>
        To Do, {name}!
      </p>
      <input type="text" name="task" value={name} placeholder="Enter task..." onChange={(e) => setName(e.target.value)} />
      &nbsp;
      <input type="button" name="add" value={'Save Task'} onChange={(e) => setName(e.target.value)} />
    </div>
  );
}

export default Welcome;
